import React, { Component } from 'react';
import styles from "../css/sidebar.module.css";
import ActiveLink from "./ActiveLink";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {  } from '@fortawesome/free-solid-svg-icons'
import {
    FacebookIcon,
    LinkedinIcon,
    TwitterIcon,
    WhatsappIcon
  } from "react-share";

class Sidebar extends Component {
    render() {
        return (
            <div>
                <aside className={styles.aside}>
                    <div className={styles.logo}>
                        <div className={styles.logoPic}>
                            
                        </div>
                        <h2>EDGIAV</h2>
                    </div>
                    <div className={styles.ul}>

                    <ActiveLink activeClassName={styles.active} href="/"><a className={styles.link} >Home</a></ActiveLink>
                    <ActiveLink activeClassName={styles.active} href="/gallery"><a className={styles.link} >Gallery</a></ActiveLink>                           
                    <ActiveLink activeClassName={styles.active} href="/blog"><a className={styles.link} >Blog</a></ActiveLink>
                    <ActiveLink activeClassName={styles.active} href="/aboutus"><a className={styles.link} >About</a></ActiveLink>
                    <ActiveLink activeClassName={styles.active} href="/contact"><a className={styles.link} >Contact</a></ActiveLink>
                         
                    </div>
                    <div className={styles.followUs}>
                        <h4>Follow us here</h4>
                        <div className={styles.followUsNetworks}>
                            <a href="https://www.facebook.com/techronx"  target="blank"><FontAwesomeIcon icon={['fab', 'facebook-f']} /></a>
                            <a href="https://www.twitter.com/mytechronx" target="blank"><TwitterIcon size={24} round={true} /></a>
                            <a href="https://www.linkedin.com/company/techronx" target="blank"><LinkedinIcon size={24} round={true}/></a>
                            <a href="https://api.whatsapp.com/send?phone=7289991556" target="blank"><WhatsappIcon size={24} round={true} /></a>
                        </div>
                    </div>
                </aside>
                <style jsx global>{`
                body {
                    margin: 0;
                    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
                    'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
                    sans-serif;
                    -webkit-font-smoothing: antialiased;
                    -moz-osx-font-smoothing: grayscale;
                }
                `}</style>

            </div>
        );
    }
}

export default Sidebar;
